// toolCode must return a string — that string is what the AI reads as the tool result
let models = [];
try {
  const raw = typeof $input === 'string' ? $input : JSON.stringify($input);
  const parsed = JSON.parse(raw);
  models = Array.isArray(parsed) ? parsed : (parsed.models || []);
} catch (e) {
  return 'Error: input must be a JSON string with a models array. Example: [{"name":"...","revenuePotential":5000,"automationFeasibility":8,"timeToRevenue":2,"riskScore":3}]';
}

if (!models.length) {
  return 'Error: models array is empty. Provide at least one business model to score.';
}

const scored = models.map(m => ({
  ...m,
  compositeScore: parseFloat((
    ((m.revenuePotential || 0) / 10000) * 0.35 +
    ((m.automationFeasibility || 5) / 10) * 0.35 +
    (1 - Math.min(m.timeToRevenue || 6, 12) / 12) * 0.20 +
    (1 - (m.riskScore || 5) / 10) * 0.10
  ).toFixed(4))
}));

scored.sort((a, b) => b.compositeScore - a.compositeScore);

// Must return a string
return JSON.stringify({ rankedModels: scored, top2: scored.slice(0, 2) }, null, 2);
