// Deactivate all scheduled workflows via n8n Admin API
const n8nHost = ($env.N8N_HOST || 'http://localhost:5678').replace(/\/$/, '');
const apiKey  = $env.N8N_API_KEY || '';

if (!apiKey) {
  throw new Error('N8N_API_KEY environment variable is not set. Cannot deactivate workflows automatically.');
}

const headers = { 'X-N8N-API-KEY': apiKey, 'Content-Type': 'application/json' };

// Step 1: List all workflows
const listResponse = await this.helpers.httpRequest({
  method: 'GET',
  url: `${n8nHost}/api/v1/workflows?limit=50&active=true`,
  headers
});

const workflows = listResponse.data || listResponse.workflows || [];
const deactivated = [];
const skipped = [];
const failed = [];

// Step 2: Deactivate each active workflow except this shutdown workflow itself
for (const wf of workflows) {
  if (wf.name && wf.name.includes('SHUTDOWN')) {
    skipped.push(wf.name);
    continue;
  }
  try {
    await this.helpers.httpRequest({
      method: 'PATCH',
      url: `${n8nHost}/api/v1/workflows/${wf.id}`,
      headers,
      body: JSON.stringify({ active: false })
    });
    deactivated.push(wf.name);
  } catch (err) {
    failed.push({ name: wf.name, error: err.message });
  }
}

return [{
  json: {
    SHUTDOWN_STATUS: failed.length === 0 ? 'COMPLETE' : 'PARTIAL',
    timestamp: new Date().toISOString(),
    workflows_deactivated: deactivated,
    workflows_skipped: skipped,
    workflows_failed: failed,
    message: `Shutdown complete. ${deactivated.length} workflow(s) deactivated. ${failed.length} failed.`
  }
}];
